import React from 'react';

import { Container, Row, Col } from 'react-bootstrap'
import CurrentWeather from './components/CurrentWeather';
import FiveDayForecast from './components/FiveDayForecast';
import './App.css';

const APPID = '8c3e7fd0f6ec033c8ecf4379bb4cce9d';
const InitialState = {
  latitude: null,
    longitude: null,
    weather: null,
    forecast: null,
    zip: ''
}

class App extends React.Component {
  constructor(props) {
    super(props);

    // Retrieve the last state
    this.state = localStorage.getItem("appState") ? JSON.parse(localStorage.getItem("appState")) : InitialState;

  }

  componentDidMount() {
    if (!navigator.geolocation) {
      alert('Geolocation is not supported by your browser');
    } else {
      navigator.geolocation.getCurrentPosition(this.success, this.error);
    }
  }

  componentWillUnmount() {
    localStorage.setItem('appState', JSON.stringify(this.state));
  }

  success = (position) => {
    const lat = position.coords.latitude;
    const lon = position.coords.longitude;

    Promise.all([
      fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&units=imperial&APPID=${APPID}`),
      fetch(`https://api.openweathermap.org/data/2.5/forecast?lat=${lat}&lon=${lon}&&units=imperial&APPID=${APPID}`),
    ])
    .then(async([weather, forecast]) => {
      const current = await weather.json();
      const future = await forecast.json();
      return [current, future]
    })   
    .then((response) => {
      this.setState({ weather: response[0] });
      this.setState({ forecast: response[1] })
    }).catch((err) => {
      console.log(err);
    });
  }

  error = () => {
    alert('Unable to retrieve your location');
  }

  handleChange(event) {
    console.log(this.state)
    // this.setState({zip: event.target.value});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.zip);
    event.preventDefault();
  }

  render() {
    return (
      <div className="App">
        <Container>
          <Row>
          <form onSubmit={this.handleSubmit}>
            <label>
              Name:
              <input type="text" value={this.state.zip} onChange={this.handleChange}/>
            </label>
            <input type="submit" value="Submit" />
          </form>
          </Row>
          <Row>
            <Col sm={4}><CurrentWeather OWMKey={APPID} weather={this.state.weather} /></Col>
            <Col sm={8}><FiveDayForecast OWMKey={APPID} forecast={this.state.forecast}/></Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default App;
