import React from 'react';
import { Card, Spinner } from 'react-bootstrap';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

function CurrentWeather(props) {
  console.log(props)
  const ticks = [];

    if (!props.forecast) {
        return (
          <Card>
            <Card.Body>
                <Spinner animation="border" role="status">
                <span className="sr-only">Loading...</span>
                </Spinner>
                <Card.Text>Loading weather forecast...</Card.Text>
            </Card.Body>
          </Card>
        )
    }

    const extractTemps = (data) => {
      const tempArray = [];

      for (const property in data) {
        tempArray.push(data[property].main.temp);
      }

      return tempArray;
    }

    const extractDates = (data) => {
      const dates = [];

      data.forEach((property, index) => {

        if ((property.dt_txt).includes("0:00:00")) {
          ticks.push(index);
        }
console.log("------", ticks)
        dates.push(new Date(property.dt_txt));
      });
//       for (const property in data) {
//         if ((data[property].dt_txt).includes("0:00:00")) {
//           console.log("in here", data[property].dt_txt)
//         }
//         dates.push(data[property].dt_txt);
//         // dates.push(new Date(data[property].dt_txt));
//       }
// console.log("--------", dates)
      return dates;
    }

    const options = {
        chart: {
            type: 'spline'
        },
        title: {
            text: props.forecast.city.name
        },
        subtitle: {
            text: 'Hourly 5 Day Forecast'
        },
        xAxis: {
            categories: extractDates(props.forecast.list),
            tickPositions: ticks,
            labels: {
              align: 'left',
              x: 3,
              y: 16,
              format: '{value: %e %b}'
            },
            gridLineWidth: 1,
        },
        yAxis: {
            title: {
                text: 'Temperature'
            },
            labels: {
                formatter: function () {
                    return this.value + '°';
                }
            },
            gridLineWidth: 0
        },
        tooltip: {
            crosshairs: true,
            shared: true
        },
        plotOptions: {
            spline: {
                marker: {
                    radius: 4,
                    lineColor: '#666666',
                    lineWidth: 1
                }
            }
        },
        series: [{
            name: 'Temperature',
            data: extractTemps(props.forecast.list)
        }]
      }

    return (
    <Card>
        <Card.Body>
            <HighchartsReact
                highcharts={Highcharts}
                options={options}
            />
        </Card.Body>
    </Card>
    );
}

export default CurrentWeather;
