import React from 'react';

import { Container, Row, Col } from 'react-bootstrap'
import CurrentWeather from './components/CurrentWeather';
import FiveDayForecast from './components/FiveDayForecast';
import './css/owfont-regular.min.css';
import './App.css';

const APPID = '8c3e7fd0f6ec033c8ecf4379bb4cce9d';

class App extends React.Component {
  state = {
    latitude: null,
    longitude: null,
    weather: null
  }

  componentDidMount() {
    if (!navigator.geolocation) {
      // status.textContent = 'Geolocation is not supported by your browser';
    } else {
      // status.textContent = 'Locating…';
      navigator.geolocation.getCurrentPosition(this.success, this.error);
    }
  }

  success = (position) => {
    const lat = position.coords.latitude;
    const lon = position.coords.longitude;

    Promise.all([
      fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&APPID=${APPID}`),
      fetch(`https://api.openweathermap.org/data/2.5/forecast?lat=${lat}&lon=${lon}&APPID=${APPID}`),
    ])
    .then(async([weather, forecast]) => {
      const current = await weather.json();
      const future = await forecast.json();
      return [current, future]
    })   
    .then((response) => {
      this.setState({ weather: response[0] });
      this.setState({ forecast: response[1] })
    }).catch((err) => {
      console.log(err);
    });
  }

  render() {
    return (
      <div className="App">
        <Container>
          <Row>
            <Col sm={4}><CurrentWeather OWMKey={APPID} weather={this.state.weather} /></Col>
            <Col sm={8}><FiveDayForecast OWMKey={APPID} /></Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default App;
