import React from 'react';
import { Card, Image, Spinner } from 'react-bootstrap';

function CurrentWeather(props) {
  const { weather } = props;
  const imperialUnit = 'ºF';

  if (!props.weather) {
    return (
      <Card>
        <Card.Body>
          <Spinner animation="border" role="status">
            <span className="sr-only">Loading...</span>
          </Spinner>
          <Card.Text>Loading current weather conditions...</Card.Text>
        </Card.Body>
      </Card>
    )
  }

  return (
    <Card>
        <Card.Body>
          <React.Fragment>
            <h3 className="secondary">{ weather.name }</h3>
            <h4 className="primary">{ weather.weather[0].main }</h4>
            <div>{ weather.weather[0].description }</div>
            <Image src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`} />
            <h3>{ weather.main.temp } {imperialUnit}</h3>
            <small>(feels like { weather.main.feels_like } {imperialUnit})</small>
          </React.Fragment>
        </Card.Body>
    </Card>
  );
}

export default CurrentWeather;
