import React from 'react';

import { Container, Row, Col } from 'react-bootstrap'
import CurrentWeather from './components/CurrentWeather';
import FiveDayForecast from './components/FiveDayForecast';
import './App.css';

const APPID = '8c3e7fd0f6ec033c8ecf4379bb4cce9d';

class App extends React.Component {
  state = {
    error: null,
    isLoaded: false,
    data: null,
  }
  componentDidMount() {
    fetch(`https://api.openweathermap.org/data/2.5/weather?q=Atlanta&APPID=${APPID}`)
      .then(res => res.text())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            data: result
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    return (
      <div className="App">
        <Container>
          <Row>
            IN here: {this.state.data}
            <Col sm={4}><CurrentWeather /></Col>
            <Col sm={8}><FiveDayForecast /></Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default App;
