import React from 'react';

import { Container, Row, Col } from 'react-bootstrap'
import CurrentWeather from './components/CurrentWeather';
import FiveDayForecast from './components/FiveDayForecast';
import './App.css';

const APPID = '8c3e7fd0f6ec033c8ecf4379bb4cce9d';

class App extends React.Component {
  state = {
    latitude: null,
    longitude: null,
  }

  componentDidMount() {
    if (!navigator.geolocation) {
      // status.textContent = 'Geolocation is not supported by your browser';
    } else {
      // status.textContent = 'Locating…';
      navigator.geolocation.getCurrentPosition(this.success, this.error);
    }
  }

  success = (position) => {
    // this.setState({ latitude: position.coords.latitude });
    // this.setState({ longitude: position.coords.longitude });

    Promise.all([
      new Promise(resolve => setTimeout(() => resolve(1), 3000)), // 1
      new Promise(resolve => setTimeout(() => resolve(2), 2000)), // 2
      new Promise(resolve => setTimeout(() => resolve(3), 1000))  // 3
    ]).then(alert); // 1,2,3 when promises are ready: each promise contributes an array member

    // try {
    //   let [weather] = await Promise.all([
    //     fetch(`https://api.openweathermap.org/data/2.5/weather?lat={${position.coords.latitude}}&lon={${position.coords.longitude}}&APPID=${APPID}`),
    //   ]);

    // }
    // catch(err) {
    //   console.log(err);
    // };
  }

  render() {
    return (
      <div className="App">
        <Container>
          <Row>
            <Col sm={4}><CurrentWeather OWMKey={APPID} lat={this.state.latitude} lon={this.state.longitude} /></Col>
            <Col sm={8}><FiveDayForecast OWMKey={APPID} /></Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default App;
