import React from 'react';
import { Card } from 'react-bootstrap';

class CurrentWeather extends React.Component {
  state = {
    error: null,
    isLoaded: false,
    data: null,
  }

  componentDidMount() {
    fetch(`https://api.openweathermap.org/data/2.5/weather?q=Atlanta&APPID=${this.props.OWMKey}`)
      .then(result => result.text())
      .then(
        (data) => {
          this.setState({
            isLoaded: true,
            data: JSON.parse(data)
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
    )
  }

  render() {

      return (
          <Card>
              <Card.Body>
                  {this.state.data}
              </Card.Body>
          </Card>
      );
  }
}

export default CurrentWeather;
