import React from 'react';
import ReactWeather from 'react-open-weather';
import { Card } from 'react-bootstrap';

class CurrentWeather extends React.Component {
  state = {
    error: null,
    isLoaded: false,
    data: null,
  }

  componentDidMount() {
      fetch(`https://api.openweathermap.org/data/2.5/weather?q=Atlanta&APPID=${this.props.APPID}`)
        .then(result => result.text())
        .then(
          (data) => {
            this.setState({
              isLoaded: true,
              data: data
            });
          },
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
      )
    }

    render() {
        return (
            <Card>
