import React from 'react';
import { Card, Image, Spinner } from 'react-bootstrap';

function CurrentWeather(props) {
  if (!props.weather) {
    return (
      <Card>
        <Card.Body>
          <Spinner animation="border" role="status">
            <span className="sr-only">Loading...</span>
          </Spinner>
          <Card.Text>Loading current weather conditions...</Card.Text>
        </Card.Body>
      </Card>
    )
  }

  return (
    <Card>
        <Card.Body>
          <React.Fragment>
            <h4>{ props.weather.name }</h4>
            <h5>{ props.weather.weather[0].main }</h5>
            <Image src={`https://openweathermap.org/img/wn/${props.weather.weather[0].icon}@2x.png`} />
            <h3>{ props.weather.main.temp }&#7506;</h3>
            <small>feels like</small>
            <div>{ props.weather.weather[0].description }</div>
          </React.Fragment>
        </Card.Body>
    </Card>
  );
}

export default CurrentWeather;
