import React from 'react';
import { Card, Spinner } from 'react-bootstrap';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

function CurrentWeather(props) {
  console.log(props)

    if (!props.forecast) {
        return (
          <Card>
            <Card.Body>
                <Spinner animation="border" role="status">
                <span className="sr-only">Loading...</span>
                </Spinner>
                <Card.Text>Loading weather forecast...</Card.Text>
            </Card.Body>
          </Card>
        )
    }

    const extractTemps = (data) => {
      const tempArray = [];

      for (const property in data) {
        tempArray.push(data[property].main.temp);
      }

      return tempArray;
    }

    const extractDates = (data) => {
      const dates = [];

      for (const property in data) {
        dates.push(new Date(data[property].dt_txt));
      }

      return dates;
    }

    const options = {
      chart: {
        scrollablePlotArea: {
            minWidth: 700
        }
    },

    data: {
        csvURL: 'https://cdn.jsdelivr.net/gh/highcharts/highcharts@v7.0.0/samples/data/analytics.csv',
        beforeParse: function (csv) {
            return csv.replace(/\n\n/g, '\n');
        }
    },

    title: {
        text: 'Daily sessions at www.highcharts.com'
    },

    subtitle: {
        text: 'Source: Google Analytics'
    },

    xAxis: {
        tickInterval: 7 * 24 * 3600 * 1000, // one week
        tickWidth: 0,
        gridLineWidth: 1,
        labels: {
            align: 'left',
            x: 3,
            y: -3
        }
    },

    yAxis: [{ // left y axis
        title: {
            text: null
        },
        labels: {
            align: 'left',
            x: 3,
            y: 16,
            format: '{value:.,0f}'
        },
        showFirstLabel: false
    }, { // right y axis
        linkedTo: 0,
        gridLineWidth: 0,
        opposite: true,
        title: {
            text: null
        },
        labels: {
            align: 'right',
            x: -3,
            y: 16,
            format: '{value:.,0f}'
        },
        showFirstLabel: false
    }],

    legend: {
        align: 'left',
        verticalAlign: 'top',
        borderWidth: 0
    },

    tooltip: {
        shared: true,
        crosshairs: true
    },

    plotOptions: {
        series: {
            cursor: 'pointer',
            marker: {
                lineWidth: 1
            }
        }
    },

    series: [{
        name: 'All sessions',
        lineWidth: 4,
        marker: {
            radius: 4
        }
    }, {
        name: 'New users'
    }]
    }

    // const options = {
    //     chart: {
    //         type: 'spline'
    //     },
    //     title: {
    //         text: props.forecast.city.name
    //     },
    //     subtitle: {
    //         text: 'Hourly 5 Day Forecast'
    //     },
    //     xAxis: {
    //         categories: extractDates(props.forecast.list),
    //         labels: {
    //           format: '{value: %e %b}'
    //         }
    //     },
    //     yAxis: {
    //         title: {
    //             text: 'Temperature'
    //         },
    //         labels: {
    //             formatter: function () {
    //                 return this.value + '°';
    //             }
    //         }
    //     },
    //     tooltip: {
    //         crosshairs: true,
    //         shared: true
    //     },
    //     plotOptions: {
    //         spline: {
    //             marker: {
    //                 radius: 4,
    //                 lineColor: '#666666',
    //                 lineWidth: 1
    //             }
    //         }
    //     },
    //     series: [{
    //         name: 'Temperature',
    //         data: extractTemps(props.forecast.list)
    //     }]
    //   }

    return (
    <Card>
        <Card.Body>
            <HighchartsReact
                highcharts={Highcharts}
                options={options}
            />
        </Card.Body>
    </Card>
    );
}

export default CurrentWeather;
