import React from 'react';
import { Card } from 'react-bootstrap';

class CurrentWeather extends React.Component {
  state = {
    error: null,
    isLoaded: false,
    data: null,
  }

  componentDidMount() {
    fetch(`https://api.openweathermap.org/data/2.5/weather?lat={${this.props.lat}}&lon={${this.props.lon}}&APPID=${this.props.OWMKey}`)
      .then(result => result.json())
      .then(
        (data) => {
          this.setState({
            isLoaded: true,
            data: data
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    if(this.state.isLoaded) {
      return (
        <Card>
            <Card.Body>
                {this.state.data.weather[0].main}
            </Card.Body>
        </Card>
      );
    } else {
      return 'Loading!'
    }
  }
}

export default CurrentWeather;
