    return (
      <div className="App">
        <Container>
          <Row>
            {/* <Col sm={4}><CurrentWeather OWMKey={APPID} /></Col>
            <Col sm={8}><FiveDayForecast OWMKey={APPID} /></Col> */}
          </Row>
        </Container>
      </div>
    );
  }
}

export default App;
