import React from 'react';
import { Card, Spinner } from 'react-bootstrap';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

function CurrentWeather(props) {
  console.log(props)

    if (!props.forecast) {
        return (
          <Card>
            <Card.Body>
                <Spinner animation="border" role="status">
                <span className="sr-only">Loading...</span>
                </Spinner>
                <Card.Text>Loading current weather conditions...</Card.Text>
            </Card.Body>
          </Card>
        )
    }

    const extractMaxTemps = (data) => {
      const tempArray = [], maxsArray = [];

      for (const property in data) {
        tempArray.push(data[property].main.temp);
      }

      const chunkedArray = chunkArrayInGroups(tempArray, 7);

      for (let i = 0; i < chunkedArray.length; i++) { 
        let slice = chunkedArray[i];
        maxsArray.push(Math.max(...slice)); 
      } 
      return maxsArray;
    }

    const extractMinTemps = (data) => {
      const tempArray = [], minsArray = [];

      for (const property in data) {
        tempArray.push(data[property].main.temp);
      }

      // const chunkedArray = chunkArrayInGroups(tempArray, 7);

      // for (let i = 0; i < chunkedArray.length; i++) { 
      //   let slice = chunkedArray[i];
      //   minsArray.push(Math.min(...slice)); 
      // } 
      return minsArray;
    }

    const extractDates = (data) => {
      const dates = [];

      for (const property in data) {
        dates.push(new Date(data[property].dt_txt));
      }

      // const chunkedArray = chunkArrayInGroups(dates, 7);

      // for (let i = 0; i < chunkedArray.length; i++) { 
      //   datesArray.push(chunkedArray[i]); 
      // } 
      console.log("++++++++", dates)
      return dates;
    }

    function chunkArrayInGroups(arr, size) {
      var averageArray = [];
      for(let i = 0; i < arr.length; i += size) {
        averageArray.push(arr.slice(i, i+size));
      }

      return averageArray;
    }

    const options = {
        chart: {
            type: 'spline'
        },
        title: {
            text: props.forecast.city.name
        },
        subtitle: {
            text: '5 Day Forecast'
        },
        xAxis: {
            categories: extractDates(props.forecast.list),
            labels: {
              format: '{value: %e.%b}'
            }
        },
        yAxis: {
            title: {
                text: 'Temperature'
            },
            labels: {
                formatter: function () {
                    return this.value + 'Â°';
                }
            }
        },
        tooltip: {
            crosshairs: true,
            shared: true
        },
        plotOptions: {
            spline: {
                marker: {
                    radius: 4,
                    lineColor: '#666666',
                    lineWidth: 1
                }
            }
        },
        series: [{
            name: 'Max',
            data: extractMaxTemps(props.forecast.list)
        }, {
          name: 'Min',
          data: extractMinTemps(props.forecast.list)
        }]
      }

    return (
    <Card>
        <Card.Body>
            <HighchartsReact
                highcharts={Highcharts}
                options={options}
            />
        </Card.Body>
    </Card>
    );
}

export default CurrentWeather;
