import React from 'react';
import { Card, Spinner } from 'react-bootstrap';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

function CurrentWeather(props) {
  console.log(props)
    if (!props.forecast) {
        return (
          <Card>
            <Card.Body>
                <Spinner animation="border" role="status">
                <span className="sr-only">Loading...</span>
                </Spinner>
                <Card.Text>Loading current weather conditions...</Card.Text>
            </Card.Body>
          </Card>
        )
    }

    const extractMaxTemps = (data) => {
      const temps = [];

      for (const property in data) {
        temps.push(data[property].main.temp_max);
      }

      const temparray = chunkArrayInGroups(temps, 7)
      console.log("-------", temparray)
      // return dates;
    }

    const extractDates = (data) => {
      const dates = [];

      for (const property in data) {
        dates.push(new Date(data[property].dt_txt));
      }

      const temparray = chunkArrayInGroups(dates,7)
      // return dates;
    }

    function chunkArrayInGroups(arr, size) {
      var myArray = [];
      for(var i = 0; i < arr.length; i += size) {
        const slice = arr.slice(i, i+size);
        Math.max(...slice)
        myArray.push(slice);
      }
      return myArray;
    }

    const options = {
        chart: {
            type: 'spline'
        },
        title: {
            text: props.forecast.city.name
        },
        subtitle: {
            text: '5 Day Forecast'
        },
        xAxis: {
            categories: extractDates(props.forecast.list),
            labels: {
              format: '{value: %e.%b}'
            }
        },
        yAxis: {
            title: {
                text: 'Temperature'
            },
            labels: {
                formatter: function () {
                    return this.value + '°';
                }
            }
        },
        tooltip: {
            crosshairs: true,
            shared: true
        },
        plotOptions: {
            spline: {
                marker: {
                    radius: 4,
                    lineColor: '#666666',
                    lineWidth: 1
                }
            }
        },
        series: [{
            data: extractMaxTemps(props.forecast.list)
        }]
      }

    return (
    <Card>
        <Card.Body>
            <HighchartsReact
                highcharts={Highcharts}
                options={options}
            />
        </Card.Body>
    </Card>
    );
}

export default CurrentWeather;
