import React from 'react';
import { Card } from 'react-bootstrap';

class CurrentWeather extends React.PureComponent {
  state = {
    error: null,
    isLoaded: false,
    data: null,
  }

  componentDidMount() {
    fetch(`https://api.openweathermap.org/data/2.5/weather?lat={${this.props.lat}}&lon={${this.props.lon}}&APPID=${this.props.OWMKey}`)
      .then(result => result.json())
      .then(
        (data) => {
          this.setState({
            isLoaded: true,
            data: data
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    console.log("--------",this.state.data)
    return <RenderWidget data = {this.state.data} />
  }
}
const RenderWidget = ({data}) => {
  if (!data) {
    return 'Loading!'
  }
  return (
    <Card>
        <Card.Body>
            {/* {this.state.data} */}
        </Card.Body>
    </Card>
  );
}
export default CurrentWeather;
