import React from 'react';

import { Container, Row, Col } from 'react-bootstrap'
import CurrentWeather from './components/CurrentWeather';
import FiveDayForecast from './components/FiveDayForecast';
import './App.css';

const OWMKEY = '8c3e7fd0f6ec033c8ecf4379bb4cce9d';
const OGKEY = '105d83d3523543558902f454f58714c6';

const InitialState = {
  city: {
    weather: null,
    forecast: null,
    name: ''
  }
}

class App extends React.Component {
  state = localStorage.getItem('appState') ? JSON.parse(localStorage.getItem('appState')) : InitialState;

  componentDidMount() {
    // if (!navigator.geolocation) {
    //   alert('Geolocation is not supported by your browser');
    // } else {
    //   navigator.geolocation.getCurrentPosition(this.success, this.error);
    // }
  }

  componentWillUnmount() {
    localStorage.setItem('appState', JSON.stringify(this.state));
  }

  success = (position) => {
    this.fetchData(position.coords.latitude, position.coords.longitude);    
  }

  fetchData = (lat, lon) => {
    Promise.all([
      fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&units=imperial&APPID=${OWMKEY}`),
      fetch(`https://api.openweathermap.org/data/2.5/forecast?lat=${lat}&lon=${lon}&&units=imperial&APPID=${OWMKEY}`),
    ])
    .then(async([weather, forecast]) => {
      const current = await weather.json();
      const future = await forecast.json();
      return [current, future]
    })   
    .then((response) => {
      this.setState({ weather: response[0] });
      this.setState({ forecast: response[1] })
    }).catch((err) => {
      console.log(err);
    });
  }

  error = () => {
    alert('Unable to retrieve your location');
  }

  handleChange = (event) => {
    this.setState({city: event.target.value});
  }

  handleSubmit = (event) => {
    if (localStorage.getItem(this.state.city.name)) {
      const item = localStorage.getItem(this.state.city.name);
      this.setState({city: item });
    } else {
      fetch(`https://api.opencagedata.com/geocode/v1/json?q=${this.state.city.name}&key=${OGKEY}`)
      .then(response => response.json())
      .then(
        (data) => {
            this.fetchData(data.results[0].geometry.lat, data.results[0].geometry.lng);
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
    event.preventDefault();
    localStorage.setItem(`${this.state.city.name}`, JSON.stringify(this.state.city));
    }
  }

  render() {
    return (
      <div className="App">
        <Container>
          <Row>
          <form onSubmit={this.handleSubmit}>
            <label>
              Name:
              <input type="text" value={this.state.city.name} onChange={this.handleChange}/>
            </label>
            <input type="submit" value="Submit" />
          </form>
          </Row>
          <Row>
            <Col sm={4}><CurrentWeather OWMKey={OWMKEY} weather={this.state.city.weather} /></Col>
            <Col sm={8}><FiveDayForecast OWMKey={OWMKEY} forecast={this.state.city.forecast}/></Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default App;
